import "./styles/App.css";
import "./styles/reset.css";
import React, { useState } from "react";
import { makeRequest } from "./api/api";
import SideMenu from './components/SideMenu/SideMenu';
import Avatar from "./assets/staticJarvis.png";
import ChatMessage from "./components/ChatMessage/ChatMessage";

function App() {
  const [input, setInput] = useState("");
  const [chatLog, setChatLog] = useState([
    {
      user: "gpt",
      message: "Como posso te ajudar hoje?",
    },
  ]);

  async function handleSubmit(e) {
    e.preventDefault();

    let response = await makeRequest({ prompt: input });

    response = response.data.split("\n").map((line) => <p>{line}</p>);

    // Cria uma cópia do chatLog atual
    let updatedChatLog = [...chatLog];

    for (let i = 0; i < response.length; i++) {
      // Espera 100 milissegundos antes de adicionar o próximo caractere (ajuste conforme necessário)
      await new Promise(resolve => setTimeout(resolve, 100));

      // Adiciona a mensagem do servidor ao chatLog
      updatedChatLog = [
        ...updatedChatLog,
        { user: "gpt", message: response[i] }
      ];

      // Atualiza o estado do chatLog
      setChatLog(updatedChatLog);
    }

    // Adiciona a mensagem do usuário ao chatLog
    setChatLog([
      ...updatedChatLog,
      { user: "me", message: `${input}` }
    ]);

    setInput("");
  }

  return (
    <div className="App">
       <SideMenu />
       <div className="background-container">
        <img src={Avatar} className="avatar-image" alt="Avatar" />
      </div>
      <section className="chatbox">
        <div className="chat-log">
          {chatLog.map((message, index) => (
            <ChatMessage key={index} message={message} />
          ))}
        </div>
        <div className="chat-input-holder">
          <form onSubmit={handleSubmit}>
            <input
              rows="1"
              className="chat-input-textarea"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            ></input>
          </form>
        </div>
      </section>
    </div>
  );
}

export default App;
