import React, { useState } from "react";
import { makeRequest } from "./api/api";
import Avatar from "./assets/staticJarvis.png";
import ChatMessage from "./components/ChatMessage/ChatMessage";
import "./styles/App.css";
import "./styles/reset.css";
import SideMenu from './components/SideMenu/SideMenu';

function App() {
  const [input, setInput] = useState("");
  const [userMessages, setUserMessages] = useState([]);
  const [chatLog, setChatLog] = useState([
    {
      user: "gpt",
      message: "Como posso te ajudar hoje?",
    },
  ]);

  async function handleSubmit(e) {
    e.preventDefault();

    let response = await makeRequest({ prompt: input });

    // Dividir a resposta em linhas
    response = response.data.split("\n");

    // Adicionar a mensagem do usuário ao histórico de mensagens do usuário
    setUserMessages([...userMessages, input]);

    // Inicializar o chatLog com a mensagem do usuário
    setChatLog([
      ...chatLog,
      { user: "me", message: `${input}` },
    ]);

    // Para cada linha da resposta
    for (let i = 0; i < response.length; i++) {
      // Esperar 100 milissegundos antes de adicionar o próximo caractere (ajuste conforme necessário)
      await typeResponse(response[i]);
    }

    // Limpar o campo de entrada
    setInput("");
  }

  // Função para simular a digitação da resposta do servidor
  async function typeResponse(line) {
    for (let i = 0; i <= line.length; i++) {
      // Esperar 100 milissegundos antes de adicionar o próximo caractere (ajuste conforme necessário)
      await new Promise(resolve => setTimeout(resolve, 20));
      // Adicionar o próximo caractere ao chatLog
      setChatLog(prevLog => [
        ...prevLog.slice(0, -1),
        { user: "gpt", message: line.slice(0, i) },
      ]);
    }
  }

  return (
    <div className="App">
      <SideMenu userMessages={userMessages} />
      <div className="background-container">
        <img src={Avatar} className="avatar-image" alt="Avatar" />
      </div>
      <div className="container">
        <section className="chatbox">
          <header>
            <h1>Bem-vindo ao Assistente Virtual</h1>
            <p>Como posso te ajudar hoje?</p>
          </header>
          <div className="chat-log">
            {chatLog.map((message, index) => (
              <ChatMessage key={index} message={message} />
            ))}
          </div>
          <div className="chat-input-holder">
            <form onSubmit={handleSubmit}>
              <input
                rows="1"
                className="chat-input-textarea"
                value={input}
                onChange={(e) => setInput(e.target.value)}
              ></input>
            </form>
          </div>
        </section>
      </div>
    </div>
  );

  /* return (
