import React from "react";
import "./ChatMessage.css";
import Avatar from "../../assets/icons/Iron_Man_icon.png";

/* {
  user: "gpt";
  message: "Crie um nome de artigo";
} */

const ChatMessage = ({ message }) => {
    return(

  <div className={
    `chat-message ${message.user === 'gpt' && "chatgpt"}`}>

     <div className='chat-message-center'>

      <div className={
        `avatar ${message.user === 'gpt' && "chatgpt"}`}
      >
        {message.user === "gpt" && (<img src="https://i.pinimg.com/564x/9d/bc/80/9dbc8095babf0279805d8ae68520701e.jpg"/>)}
      </div>

      <div className="message">
        {message.message}</div>
      </div>
  </div>
  )
};

export default ChatMessage;
