import React, { useEffect, useState } from "react";
import "./ChatMessage.css";
import Avatar from "../../assets/icons/iconJarvis.png";
import UserAvatar from "../../assets/icons/Iron_Man.png";

const ChatMessage = ({ message }) => {
  const [newMessage, setNewMessage] = useState(false);

  useEffect(() => {
    if (message.user === "gpt") {
      const timeoutId = setTimeout(() => {
        setNewMessage(true);
      }, 100);

      return () => clearTimeout(timeoutId);
    }
  }, [message.user]);

  const handleSpeakMessage = () => {
    if ('speechSynthesis' in window && 'SpeechSynthesisUtterance' in window) {
      console.log('O navegador suporta Text-to-Speech.');

      const speech = new SpeechSynthesisUtterance(message.message);
      window.speechSynthesis.speak(speech);
    } else {
      console.log('Text-to-Speech não é suportado neste navegador.');
    }
  };

  return (
    <div className={`chat-message ${message.user === 'gpt' ? "chatgpt" : "user"} ${newMessage ? "new-message" : ""}`}>
      <div className='chat-message-center'>
        <div className={`message ${message.user === 'gpt' ? "white-text align-right" : "black-text align-left"}`}>
          {message.message}
          {message.user === "gpt" && <button onClick={handleSpeakMessage}>Falar</button>}
        </div>
        {message.user === "gpt" ? (
          <div className={`avatar chatgpt`}>
            <img src={Avatar} alt="Avatar" />
          </div>
        ) : (
          <div className={`avatar user`}>
            <img src={UserAvatar} alt="User Avatar" />
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatMessage;
