import React, { useEffect, useState } from "react";
import Avatar from "./assets/staticJarvis.png";
import { makeRequest } from "./api/api";
import ChatMessage from "./components/ChatMessage/ChatMessage";
import "./styles/App.css";
import "./styles/reset.css";

function App() {
  const [input, setInput] = useState("");
  const [chatLog, setChatLog] = useState([
    {
      user: "gpt",
      message: "Como posso te ajudar hoje?",
    },
  ]);

  useEffect(() => {
    const intervalId = setInterval(() => {
      renderSmallImages();
    }, 10000); // Defina o intervalo de tempo para renderizar as imagens (em milissegundos)

    return () => clearInterval(intervalId);
  }, []); // Executa somente uma vez quando o componente é montado

  function renderSmallImages() {
    const smallImgSize = 150; // Tamanho das imagens menores
    const center = { x: window.innerWidth / 2, y: window.innerHeight / 2 }; // Coordenadas do centro da janela

    // Adiciona as imagens menores ao redor da imagem maior
    const smallImgPositions = [
      // Canto superior esquerdo
      { x: center.x - 350 - smallImgSize, y: center.y - 200 - smallImgSize },
      // Canto superior direito
      { x: center.x + 500 - smallImgSize, y: center.y - 120 - smallImgSize },
      // Canto inferior direito
      { x: center.x + 570 - smallImgSize, y: center.y + 190 },
      // Canto inferior esquerdo
      { x: center.x - 420 - smallImgSize, y: center.y + 350 },
    ];

    const smallImages = smallImgPositions.map((position, index) => (
      <img
        key={index}
        src={Avatar}
        className="small-image"
        style={{
          width: `${smallImgSize}px`,
          height: `${smallImgSize}px`,
          left: `${position.x}px`,
          top: `${position.y}px`,
        }}
        alt="Small Avatar"
      />
    ));

    return smallImages;
  }

  function handleSubmit(e) {
    e.preventDefault();

    // Seu código de manipulação do formulário aqui...

    setInput("");
  }

  return (
    <div className="App">
      <div className="background-container">
        {renderSmallImages()} {/* Renderiza as imagens pequenas */}
        <img src={Avatar} className="avatar-image" alt="Avatar" />
      </div>
      <section className="chatbox">
        <div className="chat-log">
          {chatLog.map((message, index) => (
            <ChatMessage key={index} message={message} />
          ))}
        </div>
        <div className="chat-input-holder">
          <form onSubmit={handleSubmit}>
            <input
              rows="1"
              className="chat-input-textarea"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            ></input>
          </form>
        </div>
      </section>
    </div>
  );
}

export default App;
