import React from "react";
import "./ChatMessage.css";
import Avatar from "../../assets/icons/iconJarvis.png";
import UserAvatar from "../../assets/icons/Iron_Man.png";

const ChatMessage = ({ message }) => {
  return (
    <div className={`chat-message ${message.user === 'gpt' ? "chatgpt" : "user"}`}>
      <div className='chat-message-center'>
        <div className={`avatar ${message.user === 'gpt' ? "chatgpt" : "user"}`}>
          {message.user === "gpt" ? <img src={Avatar} alt="Avatar" /> : <img src={UserAvatar} alt="User Avatar" />} {/* Condição para renderizar o avatar do GPT ou do usuário */}
        </div>
        <div className="message">
          {message.message}
        </div>
      </div>
    </div>
  );
};

export default ChatMessage;
