import { useState } from "react";
import "./styles/App.css";
import "./styles/reset.css";

import { makeRequest } from "./api/api";
import { SideMenu } from "./components/SideMenu/SideMenu";
import { ChatMessage } from "./components/ChatMessage/ChatMessage";

function App() {
  const [input, setInput] = useState("");
  const [chatlog, setChatlog] = useState([
    {
      user: "gpt",
      message: "Como posso te ajudar hoje?",
    },
  ]);

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const response = await makeRequest({ prompt: input });
      if (response && response.data) {
        const response = response.data.split("\n");

        setChatlog(prevChatlog => [
          ...prevChatlog,
          {
            user: "me",
            message: input,
          },
          {
            user: "gpt",
            message: response,
          },
        ]);

        setInput("");
      } else {
        console.error("Resposta inválida:", response);
      }
    } catch (error) {
      console.error("Erro ao fazer solicitação:", error);
      // Adicione tratamento de erro aqui, se necessário
    }
  }

  return (
    <div className="App">
      <SideMenu />
      <section className="chatbox">
        <div className="chat_log">
          {chatlog.map((message, index) => (
            <ChatMessage key={index} message={message} 
            />
          ))}
        </div>
        <div className="chat_input_holder">
          <form onSubmit={handleSubmit}>
            <input
              rows="1"
              className="chat_input_textarea"
              value={input}
              onChange={e => setInput(e.target.value)}
            />
          </form>
        </div>
      </section>
    </div>
  );
}

export default App;
