import React, { useEffect } from "react";
import Avatar from "../src/assets/staticJarvis.png"; // Verifique se o caminho está correto
import ChatMessage from "./components/ChatMessage/ChatMessage";
import "./styles/App.css";
import "./styles/reset.css";

function App() {
  useEffect(() => {
    const intervalId = setInterval(renderSmallImages, 10000); // Defina o intervalo de tempo para renderizar as imagens (em milissegundos)

    return () => clearInterval(intervalId);
  }, []); // Executa somente uma vez quando o componente é montado

  function renderSmallImages() {
    const container = document.querySelector(".small-images-container");
    container.innerHTML = ""; // Limpa o conteúdo anterior

    const containerWidth = container.offsetWidth;
    const containerHeight = container.offsetHeight;

    for (let i = 0; i < 5; i++) {
      const img = document.createElement("img");
      img.src = Avatar;
      img.classList.add("small-image");

      // Define posições aleatórias para as imagens
      const randomX = Math.floor(Math.random() * containerWidth);
      const randomY = Math.floor(Math.random() * containerHeight);
      img.style.left = `${randomX}px`;
      img.style.top = `${randomY}px`;

      container.appendChild(img);
    }

    // Adiciona uma pequena pausa antes de remover as imagens
    setTimeout(() => {
      // Gradualmente diminui a opacidade das imagens para que pareçam desaparecer suavemente
      const images = container.querySelectorAll(".small-image");
      images.forEach((img) => {
        img.style.opacity = 0;
      });

      // Remove as imagens após a transição de opacidade
      setTimeout(() => {
        container.innerHTML = ""; // Limpa o conteúdo
      }, 1000); // Ajuste conforme a duração da transição de opacidade (em milissegundos)
    }, 9000); // Ajuste conforme necessário para dar tempo suficiente para a renderização das imagens
  }

  return (
    <div className="App">
      <section className="chatbox">
        <div className="chat-log">
          <ChatMessage user="gpt" message="Como posso te ajudar hoje?" />
        </div>
      </section>

      <div className="small-images-container"></div>
    </div>
  );
}

export default App;
