import Avatar from "../src/assets/staticJarvis.png";
import React, { useState, useEffect } from "react";
import "./styles/App.css";
import "./styles/reset.css";
import { makeRequest } from "./api/api";
import SideMenu from "./components/SideMenu/SideMenu";
import ChatMessage from "./components/ChatMessage/ChatMessage";

function App() {
  const [input, setInput] = useState("");
  const [chatLog, setChatLog] = useState([
    {
      user: "gpt",
      message: "Como posso te ajudar hoje?",
    },
  ]);

  async function handleSubmit(e) {
    e.preventDefault();

    let response = await makeRequest({ prompt: input });

    response = response.data.split("\n").map((line) => <p>{line}</p>);

    setChatLog([
      ...chatLog,
      { user: "me", message: `${input}` },
      { user: "gpt", message: response },
    ]);
    setInput("");
  }

  useEffect(() => {
    const intervalId = setInterval(() => {
      // Função para renderizar 10 imagens menores
      renderSmallImages();
    }, 2000); // Define o intervalo de tempo para renderizar as imagens (em milissegundos)

    return () => clearInterval(intervalId);
  }, []); // Executa somente uma vez quando o componente é montado

  function renderSmallImages() {
    const container = document.querySelector(".small-images-container");
    container.innerHTML = ""; // Limpa o conteúdo anterior

    for (let i = 0; i < 10; i++) {
      const img = document.createElement("img");
      img.src = "../src/assets/staticJarvis.png";
      img.classList.add("small-image");
      container.appendChild(img);
    }
  }

  return (
    <div className="App">
      {/* <SideMenu></SideMenu> */}
      <section className="chatbox">
        <div className="chat-log">
          {chatLog.map((message, index) => (
            <ChatMessage key={index} message={message} />
          ))}
        </div>

        <div className="chat-input-holder">
          <form onSubmit={handleSubmit}>
            <input
              rows="1"
              className="chat-input-textarea"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            ></input>
          </form>
        </div>
      </section>

      <div className="small-images-container"></div>
    </div>
  );
}

export default App;
