import React, { useEffect, useState } from "react";
import Avatar from "../src/assets/staticJarvis.png";
import { makeRequest } from "./api/api";
import ChatMessage from "./components/ChatMessage/ChatMessage";
import "./styles/App.css";
import "./styles/reset.css";

function App() {
  const [input, setInput] = useState("");
  const [chatLog, setChatLog] = useState([
    {
      user: "gpt",
      message: "Como posso te ajudar hoje?",
    },
  ]);

  async function handleSubmit(e) {
    e.preventDefault();

    let response = await makeRequest({ prompt: input });

    response = response.data.split("\n").map((line) => <p>{line}</p>);

    setChatLog([
      ...chatLog,
      { user: "me", message: `${input}` },
      { user: "gpt", message: response },
    ]);
    setInput("");
  }

  useEffect(() => {
    const intervalId = setInterval(() => {

      renderSmallImages();
    }, 10000); // Define o intervalo de tempo para renderizar as imagens (em milissegundos)

    return () => clearInterval(intervalId);
  }, []); // Executa somente uma vez quando o componente é montado

  function renderSmallImages() {
    const container = document.querySelector(".small-images-container");
    const containerWidth = container.offsetWidth;
    const containerHeight = container.offsetHeight;
    const images = container.querySelectorAll(".small-image");

    // Atualiza a posição de cada imagem
    images.forEach((img) => {
      // Define novas posições aleatórias para as imagens
      const randomX = Math.floor(Math.random() * containerWidth);
      const randomY = Math.floor(Math.random() * containerHeight);
      img.style.transition = "transform 1s ease"; // Adiciona uma transição suave de transformação
      img.style.transform = `translate(${randomX}px, ${randomY}px)`; // Usa transformação para animar a posição
    });

    // Remova as transições após um segundo para evitar que interfiram na próxima atualização de posição
    setTimeout(() => {
      images.forEach((img) => {
        img.style.transition = "none"; // Remove a transição
      });
    }, 1000);
  }





  return (
    <div className="App">
      {/* <SideMenu></SideMenu> */}
      <section className="chatbox">
        <div className="chat-log">
          {chatLog.map((message, index) => (
            <ChatMessage key={index} message={message} />
          ))}
        </div>

        <div className="chat-input-holder">
          <form onSubmit={handleSubmit}>
            <input
              rows="1"
              className="chat-input-textarea"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            ></input>
          </form>
        </div>
      </section>

      <div className="small-images-container"></div>
    </div>
  );
}

export default App;
